import './App.css';
import Boton from "./componentes/boton"
import Contador from "./componentes/contador"
import { useState } from 'react';



function App() {
  const [useclic, setuseclic] =useState(0)
  const sumar=()=>setuseclic(useclic+1)
  const reiniciar=()=>setuseclic(0)
  return (
    <div className="App">
<div>
  <img src={require("./imagenes/FreeCodeCamp_logo.svg.png")} alt="logo" />
  </div>        
<div className='contenedorprincipal'>
<Contador 
texto={useclic}
/>
<Boton
texto="clic"
tipo={true}
suma={sumar}
/>
<Boton
texto="reiniciar"
tipo={false}
suma={reiniciar}
/>






</div>

</div>

  );
}

export default App;
